INSERT INTO `wp_photo_postmeta` VALUES (1902, 507, 'description', '<strong>Keep it simple. One package, one price.</strong>\r\n\r\n<strong>With o&amp;c you get:-</strong>\r\n\r\nAn Pre-wedding shoot or 10 8x12 prints from your wedding.\r\nTwoPhotographers for 8 hours on your wedding day.A disc of all your images to print as many times as you want.\r\nPassword protected online gallery to show your friends and family.All for 1650 Additional hours 100 per hour.\r\nPlease contact us for international wedding bookings.');
