INSERT INTO `wp_photo_posts` VALUES (151, 1, '2013-06-01 15:18:35', '2013-06-01 15:18:35', '<strong>Keep it simple. One package, one price.</strong>\r\n\r\n<strong>With o&amp;c you get:-</strong>\r\n\r\nA pre-wedding shoot or 10 8x12 prints from your wedding.\r\n\r\nTwo photographers for 8 hours on your wedding day.\r\n\r\nA disc of all your images to print as many times as you want.\r\n\r\nAn online gallery to show your friends and family.\r\n<p style="text-align: right;"><strong>All for 1650</strong></p>\r\nAdditional hours 100 per hour.\r\nPlease contact us for international wedding bookings.', 'o&c photography price', '', 'publish', 'closed', 'closed', '', 'oc-photography-price', '', '', '2013-09-23 11:53:18', '2013-09-23 11:53:18', '', 0, 'http://oacphotography.creativitea.co.uk/?post_type=price&#038;p=151', 0, 'price', '', 0);
