INSERT INTO `wp_photo_posts` VALUES (211, 1, '2013-06-21 05:42:12', '2013-06-21 05:42:12', 'Our approach to portrait photography is a relaxed one that provides an easy environment for the subject to be themselves in. We capture personality as well as images, and offer a range of products including prints, mounted photographs, canvas and photobooks.\r\n\r\nCapturing a special occasion with a photoshoot will ensure the memories remain forever. So if youre expecting a baby or have just reached your 100th birthday - and for everything in between, you can mark it with a portrait.', 'ABOUT', '', 'publish', 'closed', 'closed', '', 'about', '', '', '2013-09-20 04:07:13', '2013-09-20 04:07:13', '', 0, 'http://oacphotography.creativitea.co.uk/?post_type=portrait_content&#038;p=211', 0, 'portrait_content', '', 0);
