INSERT INTO `wp_photo_posts` VALUES (272, 1, '2008-08-26 01:05:00', '2008-08-26 01:05:00', 'It was Vilem and Monika last day in Cardiff before they moved to Manchester and what better to cement the memories than a portrait session.  We found a great blue wall as a back drop and Vilem and Monika did the rest. <br><br>Thanks for being great models guys all the best for Manchester.<br><br><a href="http://www.flickr.com/photos/thewarrells/collections/72157604678026158/"><img src="http://farm4.static.flickr.com/3151/3030995453_80b3127a46.jpg" border="0" alt=""></a><br><br><a href="http://www.flickr.com/photos/thewarrells/collections/72157604678026158/"><img src="http://farm4.static.flickr.com/3140/3030991243_17e89b28de.jpg" border="0" alt=""></a>', 'Leaving with the memories.', '', 'publish', 'open', 'open', '', 'leaving-with-the-memories', '', '', '2008-08-26 01:05:00', '2008-08-26 01:05:00', '', 0, 'http://192.168.1.84/oc/?p=272', 0, 'post', '', 0);
