INSERT INTO `wp_photo_posts` VALUES (536, 1, '2013-09-20 04:08:17', '2013-09-20 04:08:17', 'Our approach to portrait photography is a relaxed one that provides an easy environment for the subject to be themselves in. We capture personality as well as images, and offer a range of products including prints, mounted photographs, canvas and photobooks.\n\nCapturing a special occasion with a photoshoot will ensure the memories remain forever. So if youre expecting a baby or have just reached your 100th birthday - and for everything in between, you can mark it with a portrait.', 'ABOUT', '', 'inherit', 'open', 'open', '', '211-autosave', '', '', '2013-09-20 04:08:17', '2013-09-20 04:08:17', '', 211, 'http://www.oacphotography.com/?p=536', 0, 'revision', '', 0);
